import { createStore, combineReducers } from "redux";

const formReducer = (state = { formFields: [], savedForms: [] }, action) => {
  switch (action.type) {
    case "ADD_FIELD":
      return {
        ...state,
        formFields: [...state.formFields, action.payload],
      };
    case "REORDER_FIELDS":
      return {
        ...state,
        formFields: action.payload,
      };
    case "UPDATE_LABEL":
      return {
        ...state,
        formFields: state.formFields.map((field) =>
          field.id === action.payload.id
            ? { ...field, label: action.payload.newLabel }
            : field
        ),
      };
    case "UPDATE_PLACEHOLDER":
      return {
        ...state,
        formFields: state.formFields.map((field) =>
          field.id === action.payload.id
            ? { ...field, placeholder: action.payload.newPlaceholder }
            : field
        ),
      };
    case "UPDATE_OPTIONS":
      return {
        ...state,
        formFields: state.formFields.map((field) =>
          field.id === action.payload.fieldId
            ? {
                ...field,
                options:
                  action.payload.optionIndex !== null
                    ? field.options.map((option, index) =>
                        index === action.payload.optionIndex
                          ? action.payload.value
                          : option
                      )
                    : [...field.options, action.payload.value], // Add new option if index is null
              }
            : field
        ),
      };
    case "FETCH_FORMS":
      return {
        ...state,
        savedForms: action.payload, // Update the list of saved forms
      };
    case "LOAD_FORM":
      return {
        ...state,
        formFields: action.payload, // Load the form data into the editor
      };
    default:
      return state;
  }
};

const rootReducer = combineReducers({
  formFields: formReducer,
});

const store = createStore(
  rootReducer,
  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
);

export default store;
