import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { addField, reorderFields, updateLabel, updatePlaceholder } from './redux/actions/formFieldActions';
import TextInput from './components/textInput';
import TextArea from './components/textArea';
import DropDown from './components/dropDown';
import CheckBox from './components/checkBox';
import DatePicker from './components/datePicker';
import RadioButtons from './components/radioButtons';
import FileUpload from './components/fileUpload';

const App = () => {
  const dispatch = useDispatch();
  const formFields = useSelector((state) => state.formFields);
  const [selectedField, setSelectedField] = useState(null);

  const handleOnDragEnd = (result) => {
    const { destination, source } = result;

    if (!destination) {
      return;
    }

    if (source.droppableId === "sidebar" && destination.droppableId === "canvas") {
      const newField = {
        id: Date.now(),
        type: source.draggableId,
        label: "New Label",
        placeholder: "Enter placeholder",
        options: source.draggableId === "selectDropdown" || source.draggableId === "checkbox" || source.draggableId === "radioButtons" ? ["Option 1"] : [],
        required: false,
      };
      dispatch(addField(newField));
    } else if (source.droppableId === "canvas" && destination.droppableId === "canvas") {
      const reorderedFields = Array.from(formFields);
      const [movedField] = reorderedFields.splice(source.index, 1);
      reorderedFields.splice(destination.index, 0, movedField);
      dispatch(reorderFields(reorderedFields));
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <DragDropContext onDragEnd={handleOnDragEnd}>
        <Droppable droppableId="sidebar" isDropDisabled={true}>
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              style={{
                width: "200px",
                padding: "10px",
                backgroundColor: "#f0f0f0",
                borderRight: "1px solid #ccc",
              }}
            >
              <h3>Components</h3>
              <Draggable draggableId="textInput" index={0}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      padding: "8px",
                      marginBottom: "8px",
                      backgroundColor: "#fff",
                      border: "1px solid #ccc",
                      borderRadius: "4px",
                      cursor: "grab",
                    }}
                  >
                    Text Input
                  </div>
                )}
              </Draggable>
              <Draggable draggableId="textArea" index={1}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      padding: "8px",
                      marginBottom: "8px",
                      backgroundColor: "#fff",
                      border: "1px solid #ccc",
                      borderRadius: "4px",
                      cursor: "grab",
                    }}
                  >
                    Text Area
                  </div>
                )}
              </Draggable>
              <Draggable draggableId="selectDropdown" index={2}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      padding: "8px",
                      marginBottom: "8px",
                      backgroundColor: "#fff",
                      border: "1px solid #ccc",
                      borderRadius: "4px",
                      cursor: "grab",
                    }}
                  >
                    Select Dropdown
                  </div>
                )}
              </Draggable>
              <Draggable draggableId="checkbox" index={3}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      padding: "8px",
                      marginBottom: "8px",
                      backgroundColor: "#fff",
                      border: "1px solid #ccc",
                      borderRadius: "4px",
                      cursor: "grab",
                    }}
                  >
                    Checkbox
                  </div>
                )}
              </Draggable>
              <Draggable draggableId="datePicker" index={4}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      padding: "8px",
                      marginBottom: "8px",
                      backgroundColor: "#fff",
                      border: "1px solid #ccc",
                      borderRadius: "4px",
                      cursor: "grab",
                    }}
                  >
                    Date Picker
                  </div>
                )}
              </Draggable>
              <Draggable draggableId="radioButtons" index={5}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      padding: "8px",
                      marginBottom: "8px",
                      backgroundColor: "#fff",
                      border: "1px solid #ccc",
                      borderRadius: "4px",
                      cursor: "grab",
                    }}
                  >
                    Radio Buttons
                  </div>
                )}
              </Draggable>
              <Draggable draggableId="fileUpload" index={6}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      padding: "8px",
                      marginBottom: "8px",
                      backgroundColor: "#fff",
                      border: "1px solid #ccc",
                      borderRadius: "4px",
                      cursor: "grab",
                    }}
                  >
                    File Upload
                  </div>
                )}
              </Draggable>
              {provided.placeholder}
            </div>
          )}
        </Droppable>

        <Droppable droppableId="canvas">
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              style={{
                flex: 1,
                padding: "10px",
                backgroundColor: "#f9f9f9",
              }}
            >
              {formFields.map((field, index) => (
                <Draggable key={field.id} draggableId={String(field.id)} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={{
                        ...provided.draggableProps.style,
                        padding: "10px",
                        margin: "5px",
                        border: "1px solid gray",
                        backgroundColor: "#f4f4f4",
                        borderRadius: "4px",
                      }}
                      onClick={() => setSelectedField(field)}
                    >
                      {field.type === "textInput" && <TextInput {...field} />}
                      {field.type === "textArea" && <TextArea {...field} />}
                      {field.type === "selectDropdown" && <DropDown {...field} />}
                      {field.type === "checkbox" && <CheckBox {...field} />}
                      {field.type === "datePicker" && <DatePicker {...field} />}
                      {field.type === "radioButtons" && <RadioButtons {...field} />}
                      {field.type === "fileUpload" && <FileUpload {...field} />}
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

      {selectedField && (
        <div
          style={{
            width: "200px",
            padding: "10px",
            backgroundColor: "#f0f0f0",
            borderLeft: "1px solid #ccc",
          }}
        >
          <h3>Properties</h3>
          <label>Label</label>
          <input
            type="text"
            value={selectedField.label}
            onChange={(e) => dispatch(updateLabel(selectedField.id, e.target.value))}
          />
          <label>Placeholder</label>
          <input
            type="text"
            value={selectedField.placeholder}
            onChange={(e) => dispatch(updatePlaceholder(selectedField.id, e.target.value))}
          />
        </div>
      )}
    </div>
  );
};

export default App;
