import axios from 'axios';

export const addField = (field) => {
  return {
    type: "ADD_FIELD",
    payload: field,
  };
};

export const reorderFields = (fields) => {
  return {
    type: "REORDER_FIELDS",
    payload: fields,
  };
};

export const updateLabel = (id, newLabel) => {
  return {
    type: "UPDATE_LABEL",
    payload: { id, newLabel },
  };
};

export const updatePlaceholder = (id, newPlaceholder) => {
  return {
    type: "UPDATE_PLACEHOLDER",
    payload: { id, newPlaceholder },
  };
};

export const updateOptions = (fieldId, optionIndex, value) => {
  return {
    type: "UPDATE_OPTIONS",
    payload: {
      fieldId,
      optionIndex,
      value,
    },
  };
};

// Action to save the form to the backend
export const saveForm = (formName, formFields) => async (dispatch) => {
  try {
    const response = await axios.post('http://127.0.0.1:8000/api/forms/save', {
      form_name: formName,
      form_data: JSON.stringify(formFields),
    });

    console.log('Form saved successfully:', response.data);
    alert('Form saved successfully!');
  } catch (error) {
    console.error('Error saving form:', error);
    alert('Failed to save form.');
  }
};

// Action to fetch saved forms from the backend
export const fetchForms = () => async (dispatch) => {
  try {
    const response = await axios.get('http://127.0.0.1:8000/api/forms/list');
    dispatch({
      type: 'FETCH_FORMS',
      payload: response.data,
    });
  } catch (error) {
    console.error('Error fetching forms:', error);
  }
};

// Action to load a saved form into the editor
export const loadForm = (formData) => {
  return {
    type: 'LOAD_FORM',
    payload: JSON.parse(formData), // Parse the JSON string
  };
};
